import React, { useState } from "react";
import "./Styles.css"; // Import the CSS file

const Home = () => {
  const [activeTab, setActiveTab] = useState(0);

  const tabContent = [
    { title: "More About Me", content: "This is more about me!" },
    { title: "Projects", content: "Here are my projects!" },
    { title: "Connect with Me", content: "Reach out to me here!" },
  ];

  return (
    <div className="app">
      {/* Sidebar Section */}
      <aside className="sidebar">
        <h2>About Me</h2>
        <p>
          Passionate developer skilled in creating engaging and efficient web
          applications. I enjoy solving problems and building impactful digital
          experiences.
        </p>
        <h3>Contact</h3>
        <p>Email: your.email@example.com</p>
        <p>Phone: 123-456-7890</p>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        {/* Tabs */}
        <div className="tabs">
          {tabContent.map((tab, index) => (
            <button
              key={index}
              className={`tab-button ${index === activeTab ? "active" : ""}`}
              onClick={() => setActiveTab(index)}
            >
              {tab.title}
            </button>
          ))}
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {tabContent.map((tab, index) => (
            <div
              key={index}
              className={`tab-panel ${
                index === activeTab ? "active" : "inactive"
              }`}
            >
              {tab.content}
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default Home;
